function plotRankSnapshot (srStats, n, players, teamSize, matches)  #v2: switched rankT/rankM axes  dotSize = 2;  scatter(players(:,4),players(:,5), dotSize, 'filled')    plotTitle = cstrcat("Scatterplot of each player's rankM vs rankT",...  "\n", num2str(n), " players, ", num2str(round(matches*(2*teamSize/n))), " mean matches per player",...  "\nSortedness: ", num2str(srStats(end,6)));    title(plotTitle);  xlabel("rankT");  ylabel("rankM");endfunction