function plotRanks (srStats, n, teamSize, matches)  #plot ave(abs(rankT-rankM)) as a function of matches played. error bars cover  # 2 std devs of this quantity, showing that 95% of players will lie in that  # interval. lower error bars are capped such that all values are > 0.  errorbar(srStats(:,5)*(2*teamSize/n) , srStats(:,3) , min([srStats(:,3)'; 2*srStats(:,4)']) , 2*srStats(:,4), ".");    #add title and labels  plotTitle = cstrcat("Mean rank discrepancy: ", num2str(n), " players, ", num2str(matches), " total matches");  title(plotTitle);  xlabel("Average matches per player");  ylabel("Mean |rankT-rankM|, with error bars containing 95% of players");endfunction