function plotSortedness (srStats, n, teamSize, matches)  #plot sortedness as a function of matches played.  scatter(srStats(:,5)*(2*teamSize/n) , srStats(:,6));    #add title and labels  plotTitle = cstrcat("Sortedness: ", num2str(n), " players, ", num2str(matches), " total matches. ",...  "final s: ", num2str(srStats(end,6)));  title(plotTitle);  xlabel("Average matches per player");  ylabel("Sortedness: dot(rankT-meanRank, rankM-meanRank)/norm(rankT)^2");endfunction