function plotSortednessSnapshot (players, segments, n, matchesPerPlayer)  s=sortedness2(players(:,4),players(:,5),1);    sSegments=sortedness2(players(:,4), players(:,5), segments);  scatter(1:segments, sSegments, 'g', 'filled');  axis([1, segments, 0, 1]);  plotTitle = cstrcat('Global and local sortedness, ', num2str(n),...  ' players in ', num2str(segments), ' segments, ',...  num2str(matchesPerPlayer), ' mean matches per player');  title(plotTitle);  legend('local s');  hold on    s=sortedness2(players(:,4), players(:,5), 1);  sLine=[0, s; 11, s];  plot(sLine(:,1), sLine(:,2));  legend('local s', 'global s');  xlabel('# segment of player pool');  ylabel('sortedness');  hold off  endfunction