function s = sortedness(ranks1, ranks2, segments)  n=numel(ranks1);  segLength = floor(n/segments);  meanRank = (n+1)/2;  s=zeros(segments,1);  ranks1 = ranks1 - meanRank;  ranks2 = ranks2 - meanRank;    for i=1:segments    segStart = 1 + (i - 1)*segLength;    segEnd = segStart + segLength - 1;    s(i) = dot(ranks1(segStart:segEnd), ranks2(segStart:segEnd))/norm(ranks1)^2;  endfor  endfunction  